import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import { SupabaseService } from './services/supabase.service';
import { Usuario } from './models/usuario.model';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'Sistema Financeiro para Casal';
  usuarioLogado: Usuario | null = null;
  mostrarMenu = false;

  constructor(
    private router: Router,
    private supabaseService: SupabaseService
  ) {}

  ngOnInit() {
    // Verifica se o usuário está autenticado
    this.supabaseService.getCurrentUser().subscribe(user => {
      this.usuarioLogado = user as Usuario;
      this.verificarRotaAtual(this.router.url);
    });

    // Escuta mudanças de rota para controle do menu
    this.router.events
      .pipe(filter(event => event instanceof NavigationEnd))
      .subscribe((event: any) => {
        this.verificarRotaAtual(event.url);
      });
  }

  /**
   * Verifica a rota atual para determinar se o menu deve ser mostrado
   */
  private verificarRotaAtual(url: string) {
    const rotasSemMenu = ['/login', '/register', '/recuperar-senha'];
    this.mostrarMenu = !rotasSemMenu.some(rota => url.startsWith(rota));
  }

  /**
   * Realiza o logout do usuário
   */
  logout() {
    this.supabaseService.signOut().then(() => {
      this.router.navigate(['/login']);
    });
  }
}
