import { Injectable } from '@angular/core';
import { createClient, SupabaseClient, User, AuthResponse } from '@supabase/supabase-js';
import { BehaviorSubject, Observable } from 'rxjs';
import { environment } from '../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class SupabaseService {
  private supabase: SupabaseClient;
  private currentUser = new BehaviorSubject<User | null>(null);

  constructor() {
    this.supabase = createClient(
      environment.supabaseUrl,
      environment.supabaseKey
    );

    // Verificar se o usuário já está autenticado
    this.supabase.auth.onAuthStateChange((event, session) => {
      if (session?.user) {
        this.currentUser.next(session.user);
      } else {
        this.currentUser.next(null);
      }
    });

    // Carregar usuário inicial
    this.loadUser();
  }

  private async loadUser() {
    const { data } = await this.supabase.auth.getUser();
    this.currentUser.next(data.user);
  }

  // Retorna o usuário atual como um Observable
  public getCurrentUser(): Observable<User | null> {
    return this.currentUser.asObservable();
  }

  // Retorna o usuário atual diretamente
  public getCurrentUserValue(): User | null {
    return this.currentUser.value;
  }

  // Registro de novo usuário
  public async signUp(email: string, password: string): Promise<AuthResponse> {
    const response = await this.supabase.auth.signUp({
      email,
      password
    });

    if (response.data?.user) {
      this.currentUser.next(response.data.user);
    }

    return response;
  }

  // Login de usuário
  public async signIn(email: string, password: string): Promise<AuthResponse> {
    const response = await this.supabase.auth.signInWithPassword({
      email,
      password
    });

    if (response.data?.user) {
      this.currentUser.next(response.data.user);
    }

    return response;
  }

  // Logout
  public async signOut(): Promise<{ error: any }> {
    const { error } = await this.supabase.auth.signOut();
    if (!error) {
      this.currentUser.next(null);
    }
    return { error };
  }

  // Recuperação de senha
  public async resetPassword(email: string): Promise<{ data: any, error: any }> {
    return this.supabase.auth.resetPasswordForEmail(email);
  }

  // Retorna a instância do Supabase para uso direto
  public getSupabase(): SupabaseClient {
    return this.supabase;
  }

  // Operações na tabela 'contas'
  public async getConta(id: string) {
    return this.supabase
      .from('contas')
      .select('*')
      .eq('id', id)
      .single();
  }

  public async getContasDoUsuario() {
    const user = this.getCurrentUserValue();
    if (!user) return { data: null, error: 'Usuário não autenticado' };

    return this.supabase
      .from('contas')
      .select('*')
      .or(`usuario1_id.eq.${user.id},usuario2_id.eq.${user.id}`);
  }

  public async criarConta(nome: string, saldo_inicial: number, usuario2_email?: string) {
    const user = this.getCurrentUserValue();
    if (!user) return { data: null, error: 'Usuário não autenticado' };

    let usuario2_id = null;
    if (usuario2_email) {
      const { data: usuario2 } = await this.supabase
        .from('usuarios')
        .select('id')
        .eq('email', usuario2_email)
        .single();

      if (usuario2) {
        usuario2_id = usuario2.id;
      }
    }

    return this.supabase
      .from('contas')
      .insert({
        nome,
        saldo_inicial,
        saldo_atual: saldo_inicial,
        usuario1_id: user.id,
        usuario2_id
      })
      .select()
      .single();
  }

  public async atualizarConta(id: string, dados: any) {
    return this.supabase
      .from('contas')
      .update(dados)
      .eq('id', id);
  }

  public async excluirConta(id: string) {
    return this.supabase
      .from('contas')
      .delete()
      .eq('id', id);
  }

  // Operações na tabela 'transacoes'
  public async getTransacoesDaConta(contaId: string, filtros?: any) {
    let query = this.supabase
      .from('transacoes')
      .select('*')
      .eq('conta_id', contaId);

    // Aplicar filtros se fornecidos
    if (filtros) {
      if (filtros.tipo) {
        query = query.eq('tipo', filtros.tipo);
      }
      if (filtros.categoria) {
        query = query.eq('categoria', filtros.categoria);
      }
      if (filtros.dataInicio) {
        query = query.gte('data', filtros.dataInicio);
      }
      if (filtros.dataFim) {
        query = query.lte('data', filtros.dataFim);
      }
    }

    // Ordenar por data, mais recente primeiro
    query = query.order('data', { ascending: false });

    return query;
  }

  public async criarTransacao(dados: any) {
    return this.supabase
      .from('transacoes')
      .insert(dados)
      .select()
      .single();
  }

  public async atualizarTransacao(id: string, dados: any) {
    return this.supabase
      .from('transacoes')
      .update(dados)
      .eq('id', id);
  }

  public async excluirTransacao(id: string) {
    return this.supabase
      .from('transacoes')
      .delete()
      .eq('id', id);
  }

  // Obter categorias distintas
  public async getCategorias() {
    return this.supabase
      .from('transacoes')
      .select('categoria', { distinct: true });
  }

  // Resumo por categoria
  public async getResumoPorCategoria(contaId: string, periodo?: { inicio: string, fim: string }) {
    let query = this.supabase.rpc('resumo_por_categoria', { 
      p_conta_id: contaId
    });

    if (periodo) {
      query = query.eq('p_data_inicio', periodo.inicio).eq('p_data_fim', periodo.fim);
    }

    return query;
  }
}
